import "./Arows.css";
import { useNavigate } from "react-router-dom";

function Arows(props) {
  const navigate = useNavigate();
  let pathLeft = props.pathLeft;
  let pathRight = props.pathRight;

  useEffect(() => {
    const handleKeyDown = ({ key }) => {
      setKey(key);
      const character = CHARACTER_MAP[key];
      if (character.sound) {
        character.sound.play();
      }
      setCharacters([...characters, character]);
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [characters]);

  return (
    <>
      <div
        title={pathLeft}
        onClick={() => navigate(pathLeft)}
        className="arrowLeft"
      >
        <div class="arrow"></div>
      </div>
      <div
        title={pathRight}
        onClick={() => navigate(pathRight)}
        className="arrowRight"
      >
        <div class="arrow"></div>
      </div>
    </>
  );
}

export default Arows;
