import "./Arows.css";
import { useNavigate } from "react-router-dom";
const [react, activeKey, setActiveKey] = React.useState("");

function Arows(props) {
  const navigate = useNavigate();
  let pathLeft = props.pathLeft;
  let pathRight = props.pathRight;

  const [activeKey, setActiveKey] = React.useState("");
  useKeyPress("ArrowRight", navigate(pathLeft));
  useKeyPress("ArrowLeft", navigate(pathLeft));

  return (
    <>
      <div
        title={pathLeft}
        onClick={() => navigate(pathLeft)}
        className="arrowLeft"
      >
        <div class="arrow"></div>
      </div>
      <div
        title={pathRight}
        onClick={() => navigate(pathRight)}
        className="arrowRight"
      >
        <div class="arrow"></div>
      </div>
    </>
  );
}

export default Arows;
