import "./Arows.css";
import { useNavigate } from "react-router-dom";
import { useKeyDown } from "@react-hooks-library/core";
import { useState } from "react";

function Arows(props) {
  const navigate = useNavigate();
  let pathLeft = props.pathLeft;
  let pathRight = props.pathRight;

  function Demo() {
    const [posY, setPosY] = useState(0);

    useKeyDown(["w", "W", "ArrowUp"], (e) => {
      setPosY((y) => y - 20);
      e.preventDefault();
    });

    return <div style={{ transform: `translate(${posX}px, ${posY}px)` }}></div>;
  }

  return (
    <>
      <div
        title={pathLeft}
        onClick={() => navigate(pathLeft)}
        className="arrowLeft"
      >
        <div class="arrow"></div>
      </div>
      <div
        title={pathRight}
        onClick={() => navigate(pathRight)}
        className="arrowRight"
      >
        <div class="arrow"></div>
      </div>
    </>
  );
}

export default Arows;
