import { NavLink } from "react-router-dom";
import "./MainPanel.css";
import themeIcon from "./IMG/sun_W.png";
import themeIcon2 from "./IMG/moon_B.png";

function MainPanel() {
  const setBacground = () => {
    let subject = document.getElementById("preview");
    console.log(subject);

    subject.src = themeIcon2;
  };

  <div
    class="mainCanvas"
    // id="mainCanva"
    // style='background: url("./IMG/sketch-prew-05.png");'
  ></div>;

  return (
    <>
      <div className="headerMainPage">
        <img
          width="40"
          height="40"
          src={themeIcon}
          alt="themeIcon"
          // onClick={() => window.open("https://github.com/")}
        />
      </div>
      <div className="App">
        <div className="mainCanvas">
          <img id="preview" src={themeIcon} alt="preview" />
          {/* <div className="firstLineMain">code</div>
          <div className="secondLineMain">gallery</div> */}
        </div>

        <div className="mainPanel">
          <div className="panelHeader">
            Choose the sketch <br /> to work with:
          </div>
          <div className="linkList">
            <NavLink
              onMouseOver={setBacground}
              onMouseOut={setBacground}
              className="link"
              to="/sketch-01"
            >
              sketch-01
            </NavLink>
            <NavLink
              onMouseOver={() => console.log("-02")}
              className="link"
              to="/sketch-02"
            >
              sketch-02
            </NavLink>
            <NavLink
              onMouseOver={() => console.log("-03")}
              className="link"
              to="/sketch-03"
            >
              sketch-03
            </NavLink>
            <NavLink
              onMouseOver={() => console.log("-04")}
              className="link"
              to="/sketch-04"
            >
              sketch-04
            </NavLink>
            <NavLink
              onMouseOver={() => console.log("-05")}
              className="link"
              to="/sketch-05"
            >
              sketch-05
            </NavLink>
          </div>
          <div>created by: 0xLeoDev</div>
        </div>
      </div>
    </>
  );
}

export default MainPanel;
